import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

function Header() {
  const location = useLocation();
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const isActive = (path) => location.pathname === path ? 'nav-link active' : 'nav-link';

  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);
  const closeDropdown = () => setDropdownOpen(false);

  return (
    <div className="container-fluid nav-bar p-0">
      <div className="container-lg p-0">
        <nav className="navbar navbar-expand-lg bg-secondary navbar-dark">
          <Link to="/" className="navbar-brand">
            <h1 className="m-0 text-white display-4">
              <span className="text-primary">J</span>ob<span className="text-primary">Q</span>uest
            </h1>
          </Link>
          <button
            type="button"
            className="navbar-toggler"
            aria-label="Toggle navigation"
            onClick={() => {
              document.getElementById('navbarCollapse').classList.toggle('show');
            }}
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse justify-content-between" id="navbarCollapse">
            <div className="navbar-nav ml-auto py-0">
              <Link to="/" className={`nav-item ${isActive('/')}`}>Inicio</Link>
              <Link to="/about" className={`nav-item ${isActive('/about')}`}>Conócenos</Link>
              <Link to="/services" className={`nav-item ${isActive('/services')}`}>Entrevistas</Link>

              {/* Acerca de Nosotros Dropdown */}
              <div className={`nav-item dropdown ${dropdownOpen ? 'show' : ''}`}>
                <a
                  href="#!"
                  className={`nav-link dropdown-toggle ${dropdownOpen ? 'show' : ''}`}
                  role="button"
                  aria-haspopup="true"
                  aria-expanded={dropdownOpen}
                  onClick={(e) => {
                    e.preventDefault();
                    toggleDropdown();
                  }}
                >
                  Acerca de Nosotros
                </a>
                <div className={`dropdown-menu border-0 rounded-0 m-0 ${dropdownOpen ? 'show' : ''}`}>
                  <Link to="/conocenos" className="dropdown-item" onClick={closeDropdown}>Conócenos</Link>
                  <Link to="/equipo" className="dropdown-item" onClick={closeDropdown}>Nuestro Equipo</Link>
                </div>
              </div>

              <Link to="/contact" className={`nav-item ${isActive('/contact')}`}>Login</Link>
            </div>
          </div>
        </nav>
      </div>
    </div>
  );
}

export default Header;
