import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';
import logoAbout from '../assets/img/JobQuestBlanco.png';

function Header() {
  const [showLogin, setShowLogin] = useState(false);

  return (
    <>
      <style>
        {`
          header {
            background-color: #18323f;
            padding: 10px 20px;
            display: flex;
            align-items: center;
            justify-content: space-between;
            font-family: 'EB Garamond', serif;
            position: relative;
          }

          .logo img {
            height: 50px;
          }

          nav a {
            color: white;
            text-decoration: none;
            margin-left: 20px;
            font-weight: 700;
            font-family: 'EB Garamond', serif;
            transition: color 0.3s ease;
          }

          nav a:hover,
          nav a.active {
            color: #f1a523;
          }

          .login-section {
            position: relative;
            color: white;
            cursor: pointer;
            font-weight: 700;
            font-family: 'EB Garamond', serif;
          }

          .login-dropdown {
            position: absolute;
            top: 100%;
            right: 0;
            background-color: #fddd92;
            color: #18323f;
            border-radius: 5px;
            padding: 15px;
            width: 220px;
            box-shadow: 0 2px 8px rgba(0,0,0,0.2);
            z-index: 10;
          }

          .login-dropdown label {
            display: block;
            margin-bottom: 5px;
            font-weight: bold;
          }

          .login-dropdown input {
            width: 100%;
            padding: 6px 8px;
            margin-bottom: 10px;
            border: 1px solid #ccc;
            border-radius: 3px;
          }

          .login-dropdown button {
            background-color: #18323f;
            color: #fddd92;
            border: none;
            padding: 8px 12px;
            border-radius: 4px;
            cursor: pointer;
            font-weight: bold;
            width: 100%;
            transition: background-color 0.3s ease;
          }

          .login-dropdown button:hover {
            background-color: #f1a523;
            color: #18323f;
          }

          .register-link {
            margin-top: 10px;
            display: block;
            text-align: center;
            font-size: 14px;
          }

          .register-link a {
            color: #18323f;
            font-weight: bold;
            text-decoration: underline;
          }
          .register-link a:hover {
            color: #f1a523;
          }
        `}
      </style>

      <header>
        <div className="logo">
          <Link to="/">
            <img src={logoAbout} alt="JobQuest Logo" />
          </Link>
        </div>

        <nav>
          <NavLink exact to="/" activeClassName="active">Inicio</NavLink>
          <NavLink to="/conocenos" activeClassName="active">Conócenos</NavLink>
          <NavLink to="/mision" activeClassName="active">Misión y Visión</NavLink>
          <NavLink to="/valores" activeClassName="active">Valores</NavLink>
          <NavLink to="/entrevista" activeClassName="active">Entrevista</NavLink>
        </nav>

        <div className="login-section" onClick={() => setShowLogin(!showLogin)}>
          Iniciar Sesión
          {showLogin && (
            <div className="login-dropdown" onClick={e => e.stopPropagation()}>
              <form onSubmit={e => { e.preventDefault(); alert('Intento de login'); setShowLogin(false); }}>
                <label htmlFor="email">Correo electrónico</label>
                <input id="email" type="email" required placeholder="tucorreo@ejemplo.com" />
                <label htmlFor="password">Contraseña</label>
                <input id="password" type="password" required placeholder="Contraseña" />
                <button type="submit">Entrar</button>
              </form>
              <div className="register-link">
                ¿No tienes cuenta? <Link to="/registro" onClick={() => setShowLogin(false)}>Regístrate</Link>
              </div>
            </div>
          )}
        </div>
      </header>
    </>
  );
}

export default Header;
