import React from 'react';

function GuiaUso() {
  const pasos = [
    {
      titulo: '1. Crea tu cuenta',
      descripcion: 'Regístrate con tus datos personales para acceder a todas las funcionalidades y guardar tu progreso.',
      icono: 'fa-user-plus'
    },
    {
      titulo: '2. Completa tu perfil',
      descripcion: 'Agrega tu edad, área laboral deseada y otros datos para recibir simulaciones personalizadas.',
      icono: 'fa-id-card'
    },
    {
      titulo: '3. Simula entrevistas',
      descripcion: 'Accede a preguntas reales organizadas por edad y profesión. Responde y practica en tiempo real.',
      icono: 'fa-comments'
    },
    {
      titulo: '4. Analiza tu desempeño',
      descripcion: 'Obtén retroalimentación automática, revisa estadísticas y mejora tus respuestas.',
      icono: 'fa-chart-line'
    },
    {
      titulo: '5. Guarda y recupera tu historial',
      descripcion: 'Toda tu actividad se guarda de forma segura para que accedas desde cualquier dispositivo.',
      icono: 'fa-cloud-upload-alt'
    }
  ];

  return (
    <div className="py-5 garamond-text" style={{ backgroundColor: '#18323f', color: 'white' }}>
      <div className="container">
        <div className="text-center mb-5">
          <small style={etiquetaEstilo}>Instrucciones</small>
          <h1 className="garamond-bold mt-3" style={{ color: '#fff', fontSize: '40px' }}>Guía de Uso</h1>
          <hr style={{ width: '60px', borderTop: '3px solid #fddd92', margin: '10px auto' }} />
          <p style={{ maxWidth: '680px', margin: '0 auto', fontSize: '18px', color: '#ccc' }}>
            Sigue estos pasos para comenzar tu preparación con <strong>JobQuest</strong> de forma sencilla y efectiva.
          </p>
        </div>

        <div className="row">
          {pasos.map((paso, index) => (
            <div className="col-md-6 col-lg-4 mb-4" key={index}>
              <div style={cajaPasoEstilo} className="shadow-sm paso-card">
                <div className="mb-3">
                  <div style={iconoContenedor}>
                    <i className={`fas ${paso.icono}`} style={iconoEstilo}></i>
                  </div>
                </div>
                <h4 className="garamond-bold mb-2" style={{ color: '#fddd92', fontSize: '20px' }}>{paso.titulo}</h4>
                <p style={{ color: '#eee', fontSize: '16px' }}>{paso.descripcion}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="text-center mt-5">
          <a href="/login" className="btn btn-jobquest btn-lg" style={botonEstilo}>Comienza Ahora</a>
        </div>
      </div>

      {/* Estilo animado opcional */}
      <style>
        {`
          .paso-card:hover {
            transform: translateY(-5px);
            transition: all 0.3s ease-in-out;
            box-shadow: 0 10px 20px rgba(0,0,0,0.3);
          }
        `}
      </style>
    </div>
  );
}

// Estilos
const etiquetaEstilo = {
  backgroundColor: '#fddd92',
  color: '#18323f',
  padding: '6px 14px',
  fontWeight: 'bold',
  textTransform: 'uppercase',
  borderRadius: '20px',
  fontSize: '14px',
  letterSpacing: '1px'
};

const cajaPasoEstilo = {
  backgroundColor: '#1e404f',
  borderRadius: '18px',
  padding: '35px 25px',
  textAlign: 'center',
  height: '100%',
  transition: 'all 0.3s ease-in-out',
  border: '1px solid #2c5b6c'
};

const iconoContenedor = {
  backgroundColor: '#fddd92',
  width: '70px',
  height: '70px',
  borderRadius: '50%',
  margin: '0 auto',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center'
};

const iconoEstilo = {
  fontSize: '30px',
  color: '#18323f'
};

const botonEstilo = {
  backgroundColor: '#fddd92',
  color: '#18323f',
  padding: '12px 30px',
  borderRadius: '25px',
  fontWeight: 'bold',
  fontSize: '16px',
  border: 'none'
};

export default GuiaUso;
