    ]
  },
  {
    nombre: "Experiencia Laboral",
    preguntas: [
      { tipo: "texto", texto: "¿Cuál ha sido tu mayor logro profesional o académico?" },
      { tipo: "texto", texto: "¿Cómo aseguras la calidad de tu trabajo?" },
