const EntrevistaFases = () => {
  const [faseActual, setFaseActual] = useState(0);
  const [respuestas, setRespuestas] = useState({});
  const [errores, setErrores] = useState([]);
  const [mostrarResumen, setMostrarResumen] = useState(false);

  useEffect(() => {
    fetch("")
      .then((res) => res.json())
      .then((data) => {
        const faseInicial = data.faseActual || 0;
        if (faseInicial < fases.length) {
          setFaseActual(faseInicial);
        } else {
          setFaseActual(0); // O manejar el error de otra manera
        }
      });
  }, []);

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  }, [faseActual]);

  const handleRespuesta = (faseIndex, preguntaIndex, valor) => {
    setRespuestas((prev) => ({
      ...prev,
      [`${faseIndex}-${preguntaIndex}`]: valor
    }));
  };

  const validarRespuestas = () => {
    const preguntas = fases[faseActual].preguntas;
    const erroresTemp = [];

    preguntas.forEach((_, i) => {
      const valor = respuestas[`${faseActual}-${i}`];
      if (!valor || valor.trim() === "") {
        erroresTemp.push(i);
      }
    });

    setErrores(erroresTemp);
    return erroresTemp.length === 0;
  };

  const avanzar = () => {
    if (!validarRespuestas()) return;
    const nuevaFase = faseActual + 1;
    if (nuevaFase >= fases.length) {
      setMostrarResumen(true);
      return;
    }
    fetch("", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ faseActual: nuevaFase })
    }).then(() => {
      setFaseActual(nuevaFase);
      setErrores([]);
    });
  };

  const retroceder = () => {
    const nuevaFase = faseActual - 1;
    fetch("", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ faseActual: nuevaFase })
    }).then(() => {
      setFaseActual(nuevaFase);
      setErrores([]);
    });
  };

  if (mostrarResumen) {
    return (
      <div className="container">
        <h2 style={{ color: "#073239", fontFamily: "'EB Garamond', serif" }}>Gracias por completar la entrevista</h2>
        <p style={{ fontFamily: "'EB Garamond', serif" }}>Tu información ha sido registrada. Pronto te contactaremos.</p>
      </div>
    );
  }

  const fase = fases[faseActual];

  // Verificar si la fase es válida
  if (!fase) {
    return (
      <div className="container">
        <h2>Error: Fase no encontrada.</h2>
      </div>
    );
  }

  return (
    <div style={{ backgroundColor: "#bdbaba" }}>

{/* Navbar Start */}
<div className="container-fluid p-0">
  <nav className="navbar navbar-expand-lg navbar-dark" style={{ backgroundColor: "#18323f" }}>
    <div className="container d-flex justify-content-between align-items-center">
  <a href="index.html" className="navbar-brand d-flex align-items-center">
    <img
      src="/img/JobQuestBlanco.png"
      alt="Logo"
      style={{ height: "40px", marginRight: "10px" }}
    />
    <h1 className="m-0 text-white" style={{ fontSize: "1.8rem", fontFamily: "'EB Garamond', serif" }}>
      <span style={{ color: "#f1a523" }}>J</span>ob
      <span style={{ color: "#f1a523" }}>Q</span>uest
    </h1>
  </a>
      <button
        className="navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target="#navbarNav"
        aria-controls="navbarNav"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
        <ul className="navbar-nav">
          <li className="nav-item"><a className="nav-link" href="index.html">Inicio</a></li>
          <li className="nav-item"><a className="nav-link" href="Conocenos.html">Conócenos</a></li>
          <li className="nav-item"><a className="nav-link" href="FaseEntrevista.html">Entrevista</a></li>
          <li className="nav-item"><a className="nav-link" href="Valores.html">Valores</a></li>
          <li className="nav-item"><a className="nav-link" href="Login.html">Login</a></li>
        </ul>
      </div>
    </div>
  </nav>
</div>
{/* Navbar End */}

      <div className="container" style={{ padding: "20px" }}>
        <h1 style={{ color: "#18323f", fontFamily: "'EB Garamond', serif" }}>Fase {faseActual + 1}: {fase.nombre}</h1>
        <hr />

        <div className="preguntas" style={{ backgroundColor: "#bdbaba", padding: "20px", borderRadius: "5px", color: "#f1a523" }}>
          {fase.preguntas.map((pregunta, i) => {
            const key = `${faseActual}-${i}`;
            const valor = respuestas[key] || "";

            return (
<div key={i} style={{
  marginBottom: 20,
  backgroundColor: "#30685d", // nuevo fondo de la tarjeta
  padding: "20px",
  borderRadius: "5px",
  boxShadow: "0 2px 5px rgba(0,0,0,0.1)"
}}>
  <p><strong style={{
    color: "#18323f", // gris claro para el título
    fontFamily: "'EB Garamond', serif"
  }}>
    Pregunta {i + 1} de {fase.preguntas.length}
  </strong></p>
  <label style={{
    color: "#073239", // blanco para la pregunta
    fontFamily: "'EB Garamond', serif"
  }}>
    {pregunta.texto}
  </label>



                {pregunta.tipo === "texto" && (
                  <input
                    type="text"
                    className={`campo ${errores.includes(i) ? "error" : ""}`}
                    value={valor}
                    onChange={(e) => handleRespuesta(faseActual, i, e.target.value)}
                    style={{
                      width: "100%",
                      padding: "10px",
                      borderRadius: "5px",
                      border: `1px solid ${errores.includes(i) ? "red" : "#ccc"}`,
                      marginTop: "5px"
                    }}
                  />
                )}

                {pregunta.tipo === "seleccion" && (
                  <select
                    className={`campo ${errores.includes(i) ? "error" : ""}`}
                    value={valor}
                    onChange={(e) => handleRespuesta(faseActual, i, e.target.value)}
                    style={{
                      width: "100%",
                      padding: "10px",
                      borderRadius: "5px",
                      border: `1px solid ${errores.includes(i) ? "red" : "#ccc"}`,
                      marginTop: "5px"
                    }}
                  >
                    <option value="">-- Selecciona --</option>
                    {pregunta.opciones.map((op, idx) => (
                      <option key={idx} value={op}>{op}</option>
                    ))}
                  </select>
                )}

                {pregunta.tipo === "radio" && (
                  <div>
                    {pregunta.opciones.map((op, idx) => (
                      <label key={idx} style={{ display: "block", marginTop: "5px", color: "#073239", fontFamily: "'EB Garamond', serif" }}>
                        <input
                          type="radio"
                          name={key}
                          value={op}
                          checked={valor === op}
                          onChange={() => handleRespuesta(faseActual, i, op)}
                        />
                        {op}
                      </label>
                    ))}
                  </div>
                )}

                {errores.includes(i) && (
                  <div className="error-text" style={{ color: "red" }}>Esta pregunta es obligatoria</div>
                )}
              </div>
            );
          })}
        </div>

        <div className="botones" style={{ marginTop: "20px" }}>
          <button className="boton" onClick={retroceder} disabled={faseActual === 0} style={{ marginRight: "10px", padding: "10px 20px", backgroundColor: "#fddd92", border: "none", borderRadius: "5px", cursor: "pointer", transition: "background-color 0.3s" }}>
            Anterior
          </button>
          <button className="boton" onClick={avanzar} style={{ padding: "10px 20px", backgroundColor: "#fddd92", border: "none", borderRadius: "5px", cursor: "pointer", transition: "background-color 0.3s" }}>
            {faseActual === fases.length - 1 ? "Finalizar" : "Siguiente"}
          </button>
          <button
            className="boton"
            onClick={() => {
              fetch("", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ faseActual })
              });
              alert("Progreso guardado. Puedes continuar más tarde.");
            }}
            style={{ marginLeft: "10px", padding: "10px 20px", backgroundColor: "#fddd92", border: "none", borderRadius: "5px", cursor: "pointer", transition: "background-color 0.3s" }}
          >
            Salir
          </button>
        </div>
      </div>
    </div>
  );
};

export default EntrevistaFases;
