import React, { useState, useEffect } from "react";

const fases = [/* ... tus fases como ya las tienes ... */];

const EntrevistaFases = () => {
  const [faseActual, setFaseActual] = useState(0);
  const [respuestas, setRespuestas] = useState({});
  const [errores, setErrores] = useState([]);
  const [mostrarResumen, setMostrarResumen] = useState(false);

  useEffect(() => {
    fetch("")
      .then((res) => res.json())
      .then((data) => {
        const faseInicial = data.faseActual || 0;
        if (faseInicial < fases.length) {
          setFaseActual(faseInicial);
        } else {
          setFaseActual(0);
        }
      });
  }, []);

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  }, [faseActual]);

  const handleRespuesta = (faseIndex, preguntaIndex, valor) => {
    setRespuestas((prev) => ({
      ...prev,
      [`${faseIndex}-${preguntaIndex}`]: valor
    }));
  };

  const validarRespuestas = () => {
    const preguntas = fases[faseActual].preguntas;
    const erroresTemp = [];

    preguntas.forEach((_, i) => {
      const valor = respuestas[`${faseActual}-${i}`];
      if (!valor || valor.trim() === "") {
        erroresTemp.push(i);
      }
    });

    setErrores(erroresTemp);
    return erroresTemp.length === 0;
  };

  const avanzar = () => {
    if (!validarRespuestas()) return;
    const nuevaFase = faseActual + 1;
    if (nuevaFase >= fases.length) {
      setMostrarResumen(true);
      return;
    }
    fetch("", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ faseActual: nuevaFase })
    }).then(() => {
      setFaseActual(nuevaFase);
      setErrores([]);
    });
  };

  const retroceder = () => {
    const nuevaFase = faseActual - 1;
    fetch("", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ faseActual: nuevaFase })
    }).then(() => {
      setFaseActual(nuevaFase);
      setErrores([]);
    });
  };

  if (mostrarResumen) {
    return (
      <div style={{ backgroundColor: "#bdbaba", minHeight: "100vh", padding: "40px", textAlign: "center" }}>
        <h2 style={{ color: "#073239", fontFamily: "'EB Garamond', serif" }}>
          Gracias por completar la entrevista
        </h2>
        <p style={{ fontFamily: "'EB Garamond', serif", fontSize: "1.2rem" }}>
          Tu información ha sido registrada. Pronto te contactaremos.
        </p>
      </div>
    );
  }

  const fase = fases[faseActual];

  if (!fase) {
    return (
      <div style={{ backgroundColor: "#bdbaba", minHeight: "100vh" }}>
        <h2>Error: Fase no encontrada.</h2>
      </div>
    );
  }

  return (
    <div style={{ backgroundColor: "#bdbaba", minHeight: "100vh", paddingBottom: "50px" }}>
      <div style={{ padding: "20px", maxWidth: "900px", margin: "0 auto" }}>
        <h1 style={{ color: "#18323f", fontFamily: "'EB Garamond', serif" }}>
          Fase {faseActual + 1}: {fase.nombre}
        </h1>
        <hr />
        <div style={{ padding: "20px" }}>
          {fase.preguntas.map((pregunta, i) => {
            const key = `${faseActual}-${i}`;
            const valor = respuestas[key] || "";

            return (
              <div
                key={i}
                style={{
                  backgroundColor: "#18323f",
                  padding: "20px",
                  borderRadius: "10px",
                  marginBottom: "20px",
                  boxShadow: "0 3px 8px rgba(0,0,0,0.2)"
                }}
              >
                <p style={{ color: "#f1a523", fontFamily: "'EB Garamond', serif", fontSize: "1rem", marginBottom: "5px" }}>
                  <strong>Pregunta {i + 1} de {fase.preguntas.length}</strong>
                </p>
                <label style={{ color: "#fff", fontFamily: "'EB Garamond', serif", fontSize: "1.1rem" }}>
                  {pregunta.texto}
                </label>

                {pregunta.tipo === "texto" && (
                  <input
                    type="text"
                    value={valor}
                    onChange={(e) => handleRespuesta(faseActual, i, e.target.value)}
                    style={{
                      width: "95%",
                      padding: "8px 10px",
                      marginTop: "8px",
                      borderRadius: "5px",
                      border: `1px solid ${errores.includes(i) ? "#f1a523" : "#ccc"}`,
                      fontFamily: "'EB Garamond', serif",
                      fontSize: "1rem"
                    }}
                  />
                )}

                {pregunta.tipo === "radio" && (
                  <div style={{ marginTop: "5px" }}>
                    {pregunta.opciones.map((op, idx) => (
                      <label
                        key={idx}
                        style={{
                          display: "block",
                          color: "#fff",
                          fontFamily: "'EB Garamond', serif",
                          fontSize: "1rem",
                          marginTop: "5px"
                        }}
                      >
                        <input
                          type="radio"
                          name={key}
                          value={op}
                          checked={valor === op}
                          onChange={() => handleRespuesta(faseActual, i, op)}
                          style={{ marginRight: "10px" }}
                        />
                        {op}
                      </label>
                    ))}
                  </div>
                )}

                {errores.includes(i) && (
                  <div style={{ color: "#f1a523", marginTop: "5px", fontFamily: "'EB Garamond', serif" }}>
                    Esta pregunta es obligatoria
                  </div>
                )}
              </div>
            );
          })}
        </div>

        <div style={{ textAlign: "center", marginTop: "30px" }}>
          <button
            onClick={retroceder}
            disabled={faseActual === 0}
            style={{
              marginRight: "10px",
              padding: "10px 20px",
              backgroundColor: "#f1a523",
              border: "none",
              borderRadius: "5px",
              fontFamily: "'EB Garamond', serif",
              cursor: "pointer"
            }}
          >
            Anterior
          </button>
          <button
            onClick={avanzar}
            style={{
              padding: "10px 20px",
              backgroundColor: "#f1a523",
              border: "none",
              borderRadius: "5px",
              fontFamily: "'EB Garamond', serif",
              cursor: "pointer"
            }}
          >
            {faseActual === fases.length - 1 ? "Finalizar" : "Siguiente"}
          </button>
          <button
            onClick={() => {
              fetch("", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ faseActual })
              });
              alert("Progreso guardado. Puedes continuar más tarde.");
            }}
            style={{
              marginLeft: "10px",
              padding: "10px 20px",
              backgroundColor: "#f1a523",
              border: "none",
              borderRadius: "5px",
              fontFamily: "'EB Garamond', serif",
              cursor: "pointer"
            }}
          >
            Salir
          </button>
        </div>
      </div>
    </div>
  );
};

export default EntrevistaFases;
