import React, { useState, useEffect } from 'react'; // ✅ Importar hooks necesarios

function Conocenos() {
  // ⚠️ Asegúrate de tener estos arrays definidos, aquí se incluyen valores de ejemplo
  const miembros = ['Miembro 1', 'Miembro 2', 'Miembro 3', 'Miembro 4'];
  const mensajes = ['Mensaje 1', 'Mensaje 2', 'Mensaje 3'];

  const [startIndex, setStartIndex] = useState(0);
  const [mensajeIndex, setMensajeIndex] = useState(0);
  const visibleMiembros = miembros.slice(startIndex, startIndex + 3);

  useEffect(() => {
    const intervalo = setInterval(() => {
      setMensajeIndex((prev) => (prev + 1) % mensajes.length);
    }, 5000);
    return () => clearInterval(intervalo);
  }, []);

  return (
    <>
      <style>
        {`
          .garamond-text, p, li, span, a, small {
            font-family: 'EB Garamond', serif;
            font-weight: 400;
          }

          h1, h2, h3, h4, h5, h6, .garamond-bold {
            font-family: 'EB Garamond', serif;
            font-weight: 700;
          }

          .btn-jobquest {
            font-family: 'EB Garamond', serif;
            font-weight: 700;
            background-color: #fddd92;
            color: #18323f;
            padding: 12px 24px;
            border-radius: 5px;
            text-decoration: none;
            display: inline-block;
            font-size: 16px;
            transition: background-color 0.3s ease;
          }

          .btn-jobquest:hover {
            background-color: #f1a523;
          }
        `}
      </style>

      {/* Hero Section */}
      <div className="text-center py-5 garamond-text" style={{ backgroundColor: '#ffffff', color: '#18323f' }}>
        <div className="container">
          <h1 className="display-3 font-weight-bold garamond-bold">Bienvenido a JobQuest</h1>
          <p className="lead">{mensajes[mensajeIndex]}</p>

          {/* Ejemplo de mostrar miembros */}
          <div className="row mt-4">
            {visibleMiembros.map((miembro, index) => (
              <div key={index} className="col-md-4">
                <div className="p-3 border rounded">{miembro}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default Conocenos;
