import React from 'react';
import { Link } from 'react-router-dom';

// Iconos importados desde src/assets/img/
import simulacionImg from '../assets/img/SisEntre.png';
import historialImg from '../assets/img/HisEntre.png';
import analisisImg from '../assets/img/AnDes.png';
import seguridadImg from '../assets/img/SegDatos.png';
import respaldoImg from '../assets/img/Recuperacion.png';

function Servicios() {
  const servicios = [
    {
      titulo: 'Simulaciones Personalizadas',
      descripcion: 'Entrevistas adaptadas a tu perfil, edad y área laboral. Practica como en la vida real.',
      imagen: simulacionImg
    },
    {
      titulo: 'Historial de Entrevistas',
      descripcion: 'Registra automáticamente tus respuestas y entrevistas anteriores para medir tu progreso.',
      imagen: historialImg
    },
    {
      titulo: 'Análisis de Desempeño',
      descripcion: 'Obtén reportes visuales de tu rendimiento, fortalezas y áreas de mejora.',
      imagen: analisisImg
    },
    {
      titulo: 'Seguridad de Datos',
      descripcion: 'Tu información se almacena de forma privada y cifrada. Solo tú tienes acceso.',
      imagen: seguridadImg
    },
    {
      titulo: 'Respaldo y Recuperación',
      descripcion: 'Recupera tu cuenta y mantén tus datos aunque cambies de dispositivo.',
      imagen: respaldoImg
    }
  ];

  return (
    <div className="py-5 garamond-text" style={{ backgroundColor: '#ffffff', color: '#18323f' }}>
      <div className="container">
        <div className="text-center mb-5">
          <small style={etiquetaEstilo}>Nuestros Servicios</small>
          <h1 className="garamond-bold mt-3">¿Qué te ofrece JobQuest?</h1>
          <p style={{ maxWidth: '650px', margin: '0 auto', fontSize: '18px', color: '#444' }}>
            Cada funcionalidad está diseñada para acompañarte profesionalmente en tu preparación.
          </p>
        </div>

        <div className="row justify-content-center">
          {servicios.map((servicio, index) => (
            <div className="col-md-6 col-lg-4 mb-4" key={index}>
              <div style={tarjetaEstilo}>
                <img
                  src={servicio.imagen}
                  alt={servicio.titulo}
                  style={iconoImagenEstilo}
                />
                <h4 className="garamond-bold mb-2" style={{ color: '#fddd92' }}>{servicio.titulo}</h4>
                <p style={{ color: '#eee' }}>{servicio.descripcion}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="text-center mt-5">
          <Link to="/entrevista" className="btn btn-jobquest">Comienza tu entrevista</Link>
        </div>
      </div>
    </div>
  );
}

// Estilos personalizados
const etiquetaEstilo = {
  backgroundColor: '#fddd92',
  color: '#18323f',
  padding: '6px 12px',
  fontWeight: 'bold',
  textTransform: 'uppercase',
  borderRadius: '20px',
  fontSize: '14px'
};

const tarjetaEstilo = {
  backgroundColor: '#18323f', // fondo azul para las tarjetas
  borderRadius: '15px',
  padding: '30px 25px',
  textAlign: 'center',
  height: '100%',
  boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
  border: '1px solid #264d5e'
};

const iconoImagenEstilo = {
  width: '70px',
  height: '70px',
  objectFit: 'contain',
  marginBottom: '15px'
};

export default Servicios;
