import React from 'react';
import { Link } from 'react-router-dom';

function Servicios() {
  const servicios = [
    {
      titulo: 'Simulaciones Personalizadas',
      descripcion: 'Practica entrevistas adaptadas a tu edad, perfil profesional y área laboral deseada.',
      icono: '🧠'
    },
    {
      titulo: 'Historial de Entrevistas',
      descripcion: 'Guardamos automáticamente tus entrevistas simuladas para que veas tu progreso y avances.',
      icono: '📘'
    },
    {
      titulo: 'Análisis de Desempeño',
      descripcion: 'Visualiza tus fortalezas y debilidades a través de estadísticas, comparativas y retroalimentación.',
      icono: '📊'
    },
    {
      titulo: 'Datos Seguros y Privados',
      descripcion: 'Tu información está protegida y solo tú puedes acceder a tu historial y respuestas.',
      icono: '🔒'
    },
    {
      titulo: 'Recuperación Inteligente',
      descripcion: 'Conservamos tu historial incluso si cambias de dispositivo. Siempre disponible para ti.',
      icono: '♻️'
    }
  ];

  return (
    <div className="py-5 garamond-text" style={{ backgroundColor: '#ffffff', color: '#18323f' }}>
      <div className="container">
        {/* Título principal */}
        <div className="text-center mb-5">
          <small style={etiquetaEstilo}>Nuestros Servicios</small>
          <h1 className="garamond-bold mt-3">¿Qué te ofrece JobQuest?</h1>
          <p style={{ maxWidth: '650px', margin: '0 auto', fontSize: '18px', color: '#555' }}>
            Diseñado para maximizar tu preparación y ayudarte a destacar en cualquier entrevista.
          </p>
        </div>

        {/* Tarjetas de servicios */}
        <div className="row justify-content-center">
          {servicios.map((servicio, index) => (
            <div className="col-md-6 col-lg-4 mb-4" key={index}>
              <div style={tarjetaEstilo}>
                <div style={iconoEstilo}>{servicio.icono}</div>
                <h4 className="garamond-bold mb-2" style={{ color: '#18323f' }}>{servicio.titulo}</h4>
                <p style={{ color: '#333' }}>{servicio.descripcion}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Botón final */}
        <div className="text-center mt-5">
          <Link to="/entrevista" className="btn btn-jobquest">Comienza tu entrevista</Link>
        </div>
      </div>
    </div>
  );
}

// Estilos personalizados
const etiquetaEstilo = {
  backgroundColor: '#fddd92',
  color: '#18323f',
  padding: '6px 12px',
  fontWeight: 'bold',
  textTransform: 'uppercase',
  borderRadius: '20px',
  fontSize: '14px'
};

const tarjetaEstilo = {
  backgroundColor: '#f9f9f9',
  borderRadius: '15px',
  padding: '30px 25px',
  textAlign: 'center',
  height: '100%',
  boxShadow: '0 8px 16px rgba(0,0,0,0.05)',
  transition: 'all 0.3s',
  border: '1px solid #eee'
};

const iconoEstilo = {
  fontSize: '48px',
  marginBottom: '15px',
  color: '#f1a523'
};

export default Servicios;
