import React from 'react';
import { Link } from 'react-router-dom';

function Servicios() {
  const servicios = [
    {
      titulo: 'Simulaciones personalizadas',
      descripcion: 'Practica entrevistas adaptadas a tu edad, perfil profesional y área laboral deseada.',
      icono: '🧠'
    },
    {
      titulo: 'Historial de entrevistas',
      descripcion: 'Guardamos automáticamente tus entrevistas simuladas para que veas tu progreso.',
      icono: '💾'
    },
    {
      titulo: 'Seguimiento y análisis de desempeño',
      descripcion: 'Visualiza tus fortalezas y debilidades a través de estadísticas y reportes.',
      icono: '📊'
    },
    {
      titulo: 'Almacenamiento seguro de datos',
      descripcion: 'Tu información está protegida y accesible sólo para ti en todo momento.',
      icono: '📁'
    },
    {
      titulo: 'Recuperación y respaldo automático',
      descripcion: 'Recupera tu cuenta fácilmente y conserva tu historial aunque cambies de dispositivo.',
      icono: '🔐'
    }
  ];

  return (
    <div className="py-5 garamond-text" style={{ backgroundColor: '#fff', color: '#18323f' }}>
      <div className="container">
        <div className="text-center mb-5">
          <small style={{ backgroundColor: '#fddd92', padding: '5px 10px', fontWeight: 'bold' }}>Nuestros Servicios</small>
          <h1 className="garamond-bold mt-3">¿Qué te ofrece JobQuest?</h1>
          <p style={{ maxWidth: '600px', margin: '0 auto' }}>
            Cada servicio está diseñado para ayudarte a crecer profesionalmente y enfrentar entrevistas con confianza.
          </p>
        </div>
        <div className="row">
          {servicios.map((servicio, index) => (
            <div className="col-md-6 col-lg-4 mb-4" key={index}>
              <div className="p-4 h-100 shadow-sm rounded" style={{ backgroundColor: '#f9f9f9' }}>
                <div className="mb-3" style={{ fontSize: '40px' }}>{servicio.icono}</div>
                <h4 className="garamond-bold mb-2">{servicio.titulo}</h4>
                <p>{servicio.descripcion}</p>
              </div>
            </div>
          ))}
        </div>
        <div className="text-center mt-4">
          <Link to="/entrevista" className="btn btn-jobquest">Comienza tu entrevista</Link>
        </div>
      </div>
    </div>
  );
}

export default Servicios;
