import React, { useState } from 'react';

function PreguntasFrecuentes() {
  const faqs = [
    {
      pregunta: '¿Necesito pagar para usar JobQuest?',
      respuesta: 'No, es una plataforma gratuita desarrollada como parte de un proyecto educativo.'
    },
    {
      pregunta: '¿Quién puede ver mis entrevistas?',
      respuesta: 'Solo tú. Tus datos están protegidos con cifrado y son privados.'
    },
    {
      pregunta: '¿Puedo practicar más de una vez?',
      respuesta: '¡Sí! Puedes realizar entrevistas ilimitadas para practicar y mejorar.'
    },
    {
      pregunta: '¿Qué pasa si pierdo mi cuenta?',
      respuesta: 'Puedes recuperarla mediante tu correo electrónico registrado.'
    }
  ];

  const [abierta, setAbierta] = useState(null);

  const toggle = (index) => {
    setAbierta(abierta === index ? null : index);
  };

  return (
    <div style={{ backgroundColor: '#fff', padding: '60px 0', color: 'white' }}>
      <div className="container garamond-text">
        <div className="text-center mb-5">
          <small style={etiquetaEstilo}>JobQuest</small>
          <h1 className="garamond-bold mt-3">Preguntas Frecuentes</h1>
          <p style={{ maxWidth: '650px', margin: '0 auto', color: '#ccc' }}>
            Aquí respondemos las dudas más comunes sobre JobQuest.
          </p>
        </div>

        <div className="mx-auto" style={{ maxWidth: '800px' }}>
          {faqs.map((faq, index) => (
            <div key={index} style={{ marginBottom: '20px' }}>
              <div
                onClick={() => toggle(index)}
                style={{
                  backgroundColor: '#264d5e',
                  padding: '15px 20px',
                  cursor: 'pointer',
                  borderRadius: '8px',
                  fontWeight: 'bold'
                }}
              >
                {faq.pregunta}
              </div>
              {abierta === index && (
                <div
                  style={{
                    backgroundColor: '#1e404f',
                    padding: '15px 20px',
                    borderRadius: '0 0 8px 8px',
                    borderTop: '1px solid #ccc'
                  }}
                >
                  {faq.respuesta}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

const etiquetaEstilo = {
  backgroundColor: '#fddd92',
  color: '#18323f',
  padding: '6px 14px',
  borderRadius: '20px',
  fontWeight: 'bold',
  fontSize: '14px',
  textTransform: 'uppercase'
};

export default PreguntasFrecuentes;
