import React, { useState, useEffect } from 'react';

function Conocenos() {
  // Mensajes que rotan autom치ticamente
  const mensajes = [
    'Tu plataforma para prepararte para entrevistas de trabajo',
    'Simulaciones personalizadas por edad y perfil profesional',
    'Avanza en tu carrera con JobQuest'
  ];

  const [mensajeIndex, setMensajeIndex] = useState(0);

  useEffect(() => {
    const intervalo = setInterval(() => {
      setMensajeIndex((prev) => (prev + 1) % mensajes.length);
    }, 5000); // cambia cada 5 segundos
    return () => clearInterval(intervalo);
  }, []);

  return (
    <>
      <style>
        {`
          .garamond-text, p, li, span, a, small {
            font-family: 'EB Garamond', serif;
            font-weight: 400;
          }

          h1, h2, h3, h4, h5, h6, .garamond-bold {
            font-family: 'EB Garamond', serif;
            font-weight: 700;
          }

          .btn-jobquest {
            font-family: 'EB Garamond', serif;
            font-weight: 700;
            background-color: #fddd92;
            color: #18323f;
            padding: 12px 24px;
            border-radius: 5px;
            text-decoration: none;
            display: inline-block;
            font-size: 16px;
            transition: background-color 0.3s ease;
          }

          .btn-jobquest:hover {
            background-color: #f1a523;
          }
        `}
      </style>

      {/* Secci칩n de bienvenida (Hero) */}
      <div className="text-center py-5 garamond-text" style={{ backgroundColor: '#ffffff', color: '#18323f' }}>
        <div className="container">
          <h1 className="display-3 font-weight-bold garamond-bold">Con칩cenos</h1>
          <p className="lead">{mensajes[mensajeIndex]}</p>
        </div>
      </div>
    </>
  );
}

export default Conocenos;
