import React from 'react';

function GuiaUso() {
  const pasos = [
    {
      titulo: '1. Regístrate',
      descripcion: 'Crea tu cuenta con tus datos personales para empezar a simular entrevistas.',
    },
    {
      titulo: '2. Accede a tu perfil',
      descripcion: 'Inicia sesión para ver tu historial, entrevistas anteriores y configuraciones.',
    },
    {
      titulo: '3. Simula una entrevista',
      descripcion: 'Selecciona tu edad y área profesional. Luego responde preguntas en cuatro fases clave.',
    },
    {
      titulo: '4. Evalúa tu progreso',
      descripcion: 'Revisa tus resultados, analiza tus respuestas y sigue practicando.',
    }
  ];

  return (
    <div style={{ backgroundColor: '#f5f5f5', padding: '60px 0' }}>
      <div className="container garamond-text">
        <div className="text-center mb-5">
          <small style={etiquetaEstilo}>Guía de Uso</small>
          <h1 className="garamond-bold mt-3" style={{ color: '#18323f' }}>¿Cómo utilizar JobQuest?</h1>
          <p style={{ maxWidth: '700px', margin: '0 auto', color: '#444' }}>
            Sigue estos pasos para comenzar tu experiencia en el simulador.
          </p>
        </div>

        {pasos.map((paso, index) => (
          <div className="row align-items-center mb-5" key={index}>
            {index % 2 === 0 ? (
              <>
                <div className="col-md-6">
                  <div style={cardEstiloClaro}>
                    <h3 className="garamond-bold" style={{ color: '#18323f' }}>{paso.titulo}</h3>
                    <p style={{ color: '#444' }}>{paso.descripcion}</p>
                  </div>
                </div>
                <div className="col-md-6 text-center">
                  <div style={iconoPaso}>{index + 1}</div>
                </div>
              </>
            ) : (
              <>
                <div className="col-md-6 text-center">
                  <div style={iconoPaso}>{index + 1}</div>
                </div>
                <div className="col-md-6">
                  <div style={cardEstiloClaro}>
                    <h3 className="garamond-bold" style={{ color: '#18323f' }}>{paso.titulo}</h3>
                    <p style={{ color: '#444' }}>{paso.descripcion}</p>
                  </div>
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

const etiquetaEstilo = {
  backgroundColor: '#fddd92',
  color: '#18323f',
  padding: '6px 14px',
  borderRadius: '20px',
  fontWeight: 'bold',
  fontSize: '14px',
  textTransform: 'uppercase'
};

const iconoPaso = {
  backgroundColor: '#18323f',
  color: '#fff',
  fontSize: '40px',
  width: '80px',
  height: '80px',
  borderRadius: '50%',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  margin: '0 auto'
};

const cardEstiloClaro = {
  backgroundColor: '#ffffff',
  padding: '30px',
  borderRadius: '10px',
  boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
};

export default GuiaUso;
